function convertToUtc(str,format){if(typeof(format)==='undefined')format='YYYY-MM-DD HH:mm:ss';if(str)return moment(str).utc().format(format);return str}
function convertToLocal(str,format){if(typeof(format)==='undefined')format='YYYY-MM-DD HH:mm:ss';if(str)return moment(moment.utc(str).toDate()).format(format);return str}